use anyhow::Result;

use aoc_utils::*;

pub const CARAT: u8 = b'^';
pub const OPEN: u8 = b'.';
pub const BLOCKED: u8 = b'#';

pub fn read_data(data: &str) -> Result<(Matrix<u8>, Point)> {
    let matrix = Matrix::<u8>::try_from(data)?;
    let mut x = 0;
    let mut y = 0;

    // Find the starting point, indicated by the '^'
    for row in 0..matrix.len() {
        for col in 0..matrix[0].len() {
            if matrix[row][col] == CARAT {
                x = col;
                y = row;
                break;
            }
        }
    }

    let start_pos = Point::from((x, y));
    Ok((matrix, start_pos))
}

